import 'package:awesome_dialog/awesome_dialog.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:meta/meta.dart';import 'package:pinput/pinput.dart';import 'package:sarty_app/view/screens/chats/chatScreen.dart';import 'package:sarty_app/view/screens/home/home_screen.dart';import 'package:sarty_app/view/widgets/constant.dart';class VerifyCodeController extends GetxController {  TextEditingController pinController = TextEditingController();  final defaultPinTheme = PinTheme(    width: 56,    height: 56,    decoration: BoxDecoration(        color: const Color(0xFFFFFFFF),        borderRadius: BorderRadius.circular(5),        border: Border.all(color: const Color(0xFFEEEEEE))),  );  final focusPinTheme = PinTheme(    width: 56,    height: 56,    decoration: BoxDecoration(        borderRadius: BorderRadius.circular(5),        color: const Color(0xFFFFFFFF),        border: Border.all()),  );  bool isLoading = false;  verify(context) {    isLoading = true;    update();    Future.delayed(const Duration(seconds: 2)).then((value) {      isLoading = false;      update();      GoTo_HomeScreen(context);    });  }  GoTo_HomeScreen(context) {    AwesomeDialog(      context: context,      dialogType: DialogType.success,      animType: AnimType.rightSlide,      autoHide: const Duration(milliseconds: 1300),      title: 'تم تسجيلك بنجاح',      titleTextStyle: const TextStyle(        fontFamily: 'Tajawal',        fontSize: 16,      ),    ).show().then((value) {      push(context,  ChatScreen());    });  }}