import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:meta/meta.dart';import 'package:sarty_app/core/data/staticData.dart';import 'package:sarty_app/view/screens/account/create_account_screen.dart';import 'package:sarty_app/view/widgets/constant.dart';class OnBoardingController extends GetxController {  int currentIndexPage = 0;  PageController pageController = PageController(initialPage: 0);  next(context) {    currentIndexPage++;    if (currentIndexPage > StaticData.on_boarding_data.length - 1) {      GoTo_CreateAccountScreen(context);    }    pageController.animateToPage(      currentIndexPage,      duration: const Duration(milliseconds: 800),      curve: Curves.fastOutSlowIn,    );    // update();  }  skip(context) {    pageController        .animateToPage(      StaticData.on_boarding_data.length - 1 ,      duration: const Duration(milliseconds: 800),      curve: Curves.fastOutSlowIn,    )        .then((value) {      GoTo_CreateAccountScreen(context);    });  }  GoTo_CreateAccountScreen(context) {    return push(      context,      const CreateAccountScreen(),    );  }}