import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:shimmer/shimmer.dart';void main() {  runApp(const MyApp());}class MyApp extends StatelessWidget {  const MyApp({super.key});  @override  Widget build(BuildContext context) {    return const MaterialApp(      debugShowCheckedModeBanner: false,      home: AppBarClipper(),    );  }}class AppBarClipper extends StatelessWidget {  const AppBarClipper({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        toolbarHeight: 200,        backgroundColor: Colors.transparent,        elevation: 0,        flexibleSpace: ClipPath(          clipper: MyClipper(),          child: Container(            color: Colors.purple,            width: double.infinity,            child: const Center(              child: Text(                'Hello',                style: TextStyle(                  letterSpacing: 15,                  fontSize: 40,                  color: Colors.white,                  fontWeight: FontWeight.bold,                ),                // textScaleFactor: 2,              ),            ),          ),        ),      ),    );  }}class MyClipper extends CustomClipper<Path> {  @override  Path getClip(Size size) {    var path = Path();    double width = size.width;    double height = size.height;    path.lineTo(0, height - 100);    path.quadraticBezierTo(width / 2, height + 100, width, height - 100);    path.lineTo(width, 0);    path.close();    return path;  }  @override  bool shouldReclip(covariant CustomClipper oldClipper) {    return false;  }}