import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:shimmer/shimmer.dart';void main() {  SystemChrome.setSystemUIOverlayStyle(const SystemUiOverlayStyle(    statusBarColor: Colors.white,  ));  runApp(const MyApp());}class MyApp extends StatelessWidget {  const MyApp({super.key});  @override  Widget build(BuildContext context) {    return const MaterialApp(      debugShowCheckedModeBanner: false,      home: ShimmerLoadingScreen(),    );  }}class ShimmerLoadingScreen extends StatelessWidget {  const ShimmerLoadingScreen({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.white,      body: SizedBox(        height: MediaQuery.of(context).size.height,        child: Shimmer.fromColors(          baseColor: Colors.grey.withOpacity(0.2),          highlightColor: Colors.white10.withOpacity(0.9),          direction: ShimmerDirection.ltr,          child: const ShimmerListLoadingUi(),        ),      ),    );  }}class ShimmerListLoadingUi extends StatelessWidget {  const ShimmerListLoadingUi({super.key});  @override  Widget build(BuildContext context) {    return ListView.builder(      itemCount: 7,      itemBuilder: (context, index) =>  ShimmerItemLoadingUi(),    );  }}class ShimmerItemLoadingUi extends StatelessWidget {  Decoration decoration = BoxDecoration(    borderRadius: BorderRadius.circular(5),    color: Colors.grey,  );  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(        horizontal: 30,        vertical: 15,      ),      child: Row(        children: [          Container(            height: 100,            width: 100,            decoration: decoration,          ),          Expanded(              child: Padding(            padding: const EdgeInsets.symmetric(              horizontal: 10,            ),            child: Column(              mainAxisAlignment: MainAxisAlignment.start,              crossAxisAlignment: CrossAxisAlignment.stretch,              children: [                Container(                  height: 15,                  decoration: decoration,                  // color: Colors.grey,                ),                SizedBox(                  height: 15,                ),                Container(                  height: 15,                  decoration: decoration,                ),                SizedBox(                  height: 20,                ),                Row(                  children: [                    Flexible(                      flex: 1,                      child: Container(                        height: 15,                        decoration: decoration,                      ),                    ),                    SizedBox(                      width: 20,                    ),                    Flexible(                      flex: 1,                      child: Container(                        height: 15,                        decoration: decoration,                      ),                    ),                  ],                ),              ],            ),          )),        ],      ),    );  }}