import 'dart:convert';import 'package:http/http.dart' as http;class ApiServices {  static const base_url = 'api.nasa.gov';  static Future<Map<String, dynamic>> getData() async {    try {      Uri uri = Uri.https(        base_url,        '/planetary/apod',        {'api_key': 'sfl8Om7g3Vka0UqhQaIfiIoYuhDQKtq3pl1rjN9p'},      );      var response = await http.get(uri);      if (response.statusCode == 200) {        Map<String, dynamic> ResponseBody = jsonDecode(response.body);        return ResponseBody;      }    } catch (e) {      throw Exception(e);    }    throw Exception('Something Error');  }}class ApiServices2 { static const String baseUrl = 'api.nasa.gov';  static Future<dynamic> getData(Uri uri) async {    var response = await http.get(uri);    if (response.statusCode == 200) {      final responseBody = jsonDecode(response.body);      return responseBody;    } else {      throw Exception('');    }  }}