import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:flutter_svg/flutter_svg.dart';import 'package:get/get.dart';import 'package:sarty_app/controller/create_verify_account_controller/verify_code_controller.dart';import 'package:sarty_app/view/screens/on_boarding_screen/on_boarding_screen.dart';import 'package:sarty_app/view/widgets/constant.dart';import 'package:pinput/pinput.dart';class VerifyAccountScreen extends StatelessWidget {  const VerifyAccountScreen({super.key});  @override  Widget build(BuildContext context) {    Get.put(VerifyCodeController());    return Scaffold(      backgroundColor: const Color(0xFFFFFFFF),      body: SafeArea(        child: Padding(          padding: EdgeInsets.only(            top: 121.h,          ),          child: SingleChildScrollView(            child: GetBuilder<VerifyCodeController>(builder: (logic) {              return Column(                crossAxisAlignment: CrossAxisAlignment.center,                children: [                  const SizedBox(                    width: double.infinity,                  ),                  SizedBox(                    width: 183.w,                    height: 154.h,                    child: SvgPicture.asset('assets/icons/phone_verify.svg'),                  ),                  space_h(48.h),                  Text(                    'كود التحقق',                    style: TextStyle(                      fontSize: 18.sp,                      fontWeight: FontWeight.w500,                      fontFamily: 'Tajawal',                    ),                  ),                  space_h(27.h),                  Text(                    'قم بإرسال كود التحقق المرسل إلى بريد\n\n sarty@gmail.com',                    style: TextStyle(                      fontSize: 14.sp,                      fontWeight: FontWeight.w500,                      fontFamily: 'Tajawal',                    ),                    textAlign: TextAlign.center,                  ),                  space_h(27.h),                  Pinput(                    controller: logic.pinController,                    length: 5,                    toolbarEnabled: false,                    defaultPinTheme: logic.defaultPinTheme,                    focusedPinTheme: logic.focusPinTheme,                    onCompleted: (code) {                      logic.verify(context);                    },                  ),                  space_h(27.h),                  const CustomRichText(),                  space_h(100.h),                  Visibility(                      visible: logic.isLoading,                      child: const CustomLoadingButton()),                ],              );            }),          ),        ),      ),    );  }}class CustomLoadingButton extends StatelessWidget {  const CustomLoadingButton({super.key});  @override  Widget build(BuildContext context) {    return SizedBox(        width: 327.w,        height: 50.h,        child: Stack(          children: [            CustomButton('يتم التحقق', () {}),            Positioned(                bottom: 15.h,                right: 50.w,                child: SvgPicture.asset('assets/icons/verify_loading.svg')),          ],        ));  }}class CustomRichText extends StatelessWidget {  const CustomRichText({super.key});  @override  Widget build(BuildContext context) {    return RichText(      text: TextSpan(        children: [          TextSpan(            text: 'لم أستلم كود التحقق ؟',            style: TextStyle(              fontFamily: 'Tajawal',              color: Colors.black,              fontSize: 12.sp,              fontWeight: FontWeight.w500,            ),          ),          TextSpan(            text: '  يرجى إعادة الإرسال',            style: TextStyle(              fontFamily: 'Tajawal',              color: Color(0xFF0054D2),              fontSize: 12.sp,              fontWeight: FontWeight.w500,            ),          ),        ],      ),    );  }}