import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import 'package:lottie/lottie.dart';import 'package:sarty_app/controller/greeting/choose_lang_controller.dart';import 'package:sarty_app/view/widgets/constant.dart';import 'package:sarty_app/view/widgets/splash_screen_wisgets.dart';class ChooseLangScreen extends StatelessWidget {  const ChooseLangScreen({super.key});  @override  Widget build(BuildContext context) {    Get.put(ChooseLangController());    return const Scaffold(      body: BodyChooseLangScreen(),    );  }}class BodyChooseLangScreen extends StatelessWidget {  const BodyChooseLangScreen({super.key});  @override  Widget build(BuildContext context) {    return SafeArea(      child: Container(        width: double.infinity,        padding: EdgeInsets.only(          top: 174.h,          right: 66.w,          left: 49.w,          bottom: 123.h,        ),        child: GetBuilder<ChooseLangController>(builder: (logic) {          return Column(            children: [              const CustomLogoLanguageScreen(),              space_h(94.h),              const CustomChooseAppLangText(),              space_h(20.h),              ...List.generate(2, (index) => CustomLanguageCard(index)),              ForwardToOnBoarding(logic),              const Expanded(child: FooterWidgetInChooseLang()),            ],          );        }),      ),    );  }}