import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import 'package:sarty_app/controller/greeting/on_boarding_controller.dart';import 'package:sarty_app/core/data/staticData.dart';import 'package:sarty_app/view/widgets/constant.dart';import 'package:smooth_page_indicator/smooth_page_indicator.dart';class OnBoardingScreen extends StatelessWidget {  const OnBoardingScreen({super.key});  @override  Widget build(BuildContext context) {    return const Scaffold(      body: BodyOnBoardingScreen(),    );  }}class BodyOnBoardingScreen extends StatelessWidget {  const BodyOnBoardingScreen({super.key});  @override  Widget build(BuildContext context) {    return Directionality(        textDirection: TextDirection.rtl,        child: GetBuilder<OnBoardingController>(            init: OnBoardingController(),            builder: (logic) {              return PageView.builder(                onPageChanged: (index) {                  // NeverScrollableScrollPhysics                },                controller: logic.pageController,                physics: const NeverScrollableScrollPhysics(),                itemCount: StaticData.on_boarding_data.length,                itemBuilder: (context, index) {                  return CustomPageViewItem(logic, index);                },              );            }));  }}class CustomPageViewItem extends StatelessWidget {  OnBoardingController logic;  int index;  CustomPageViewItem(this.logic, this.index);  @override  Widget build(BuildContext context) {    return Container(      padding: EdgeInsetsDirectional.only(        top: 97.h,        bottom: 84.h,        end: 38.w,        start: 51.w,      ),      child: Column(        children: [          GestureDetector(            onTap: () {              logic.skip(context);            },            child: Align(                alignment: AlignmentDirectional.centerStart,                child: Text(                  'تخطي',                  style: TextStyle(fontSize: 14.sp, fontFamily: 'Tajawal'),                )),          ),          space_h(149.h),          SizedBox(            width: 385.w,            height: 291.h,            child: Image.asset(StaticData.on_boarding_data[index].img),          ),          space_h(93.h),          Align(            alignment: Alignment.center,            child: Text(              StaticData.on_boarding_data[index].title,              style: TextStyle(                fontSize: 16.sp,                fontFamily: 'Tajawal',                fontWeight: FontWeight.w500,              ),            ),          ),          space_h(99.h),          SmoothPageIndicator(            controller: logic.pageController,            count: 2,            axisDirection: Axis.horizontal,            effect: const ExpandingDotsEffect(              dotHeight: 8,              dotWidth: 8,            ),          ),          space_h(52.h),          CustomButton('التالي', () {            logic.next(context);          }),        ],      ),    );  }}class CustomButton extends StatelessWidget {  String title;  Function()? function;  CustomButton(this.title, this.function);  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: function,      child: Container(        decoration: BoxDecoration(          color: const Color(0xFF0054D2),          borderRadius: BorderRadius.circular(5),          boxShadow: const [            BoxShadow(              offset: Offset(0, 10),              blurRadius: 20,              color: Colors.grey,            ),          ],        ),        width: double.infinity,        height: 50.h,        child: Center(          child: Text(            title,            style: const TextStyle(              color: Colors.white,              fontSize: 18,              fontFamily: 'Tajawal',              fontWeight: FontWeight.w500,            ),          ),        ),      ),    );  }}