import 'dart:async';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:lottie/lottie.dart';import 'package:page_transition/page_transition.dart';import 'package:sarty_app/view/screens/choose_lang/choose_lang_screen.dart';import 'package:sarty_app/view/widgets/constant.dart';class SplashScreen extends StatefulWidget {  const SplashScreen({super.key});  @override  State<SplashScreen> createState() => _SplashScreenState();}class _SplashScreenState extends State<SplashScreen> {  initSplashAndNavigator() {    Future.delayed(const Duration(seconds: 3)).then((value) {      push(context, const ChooseLangScreen(), replacement: true);    });  }  @override  void initState() {    // TODO: implement initState    initSplashAndNavigator();    super.initState();  }  @override  Widget build(BuildContext context) {    return const Scaffold(      backgroundColor: Color(0xFFFFFFFF),      body: BodySplashScreen(),    );  }}class BodySplashScreen extends StatelessWidget {  const BodySplashScreen({super.key});  @override  Widget build(BuildContext context) {    return Center(      child: Column(        mainAxisAlignment: MainAxisAlignment.end,        children: [          Image.asset('assets/logo/logo_sarty.png'),          SizedBox(            height: 150.h,          ),          Padding(            padding: EdgeInsets.only(              bottom: 50.h,            ),            child: SizedBox(              height: 150.h,              width: 150.h,              child: Lottie.asset('assets/logo/lottie_loading.json'),            ),          ),        ],      ),    );  }}class MyCustomSplashScreen extends StatefulWidget {  @override  _MyCustomSplashScreenState createState() => _MyCustomSplashScreenState();}class _MyCustomSplashScreenState extends State<MyCustomSplashScreen>    with TickerProviderStateMixin {  double _fontSize = 2;  double _containerSize = 1.5;  double _textOpacity = 0.0;  double _containerOpacity = 0.0;  late AnimationController _controller;  late Animation<double> animation1;  @override  void initState() {    super.initState();    _controller =        AnimationController(vsync: this, duration: Duration(seconds: 3));    animation1 = Tween<double>(begin: 40, end: 20).animate(CurvedAnimation(        parent: _controller, curve: Curves.fastLinearToSlowEaseIn))      ..addListener(() {        setState(() {          _textOpacity = 1.0;        });      });    _controller.forward();    Timer(Duration(seconds: 2), () {      setState(() {        _fontSize = 1.06;      });    });    Timer(Duration(seconds: 2), () {      setState(() {        _containerSize = 2;        _containerOpacity = 1;      });    });    Timer(Duration(seconds: 4), () {      setState(() {        Navigator.pushReplacement(context, PageTransition(SecondPage()));      });    });  }  @override  void dispose() {    _controller.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    double _width = MediaQuery.of(context).size.width;    double _height = MediaQuery.of(context).size.height;    return Scaffold(      backgroundColor: const Color(0xFFFFFFFF),      // backgroundColor: Colors.deepPurple,      body: Stack(        children: [          Column(            children: [              AnimatedContainer(                  duration: Duration(milliseconds: 2000),                  curve: Curves.fastLinearToSlowEaseIn,                  height: _height / _fontSize),              AnimatedOpacity(                duration: Duration(milliseconds: 1000),                opacity: _textOpacity,                child: Text(                  'Sarty App',                  style: TextStyle(                    color: Colors.black,                    fontWeight: FontWeight.bold,                    fontFamily: 'Tajwal',                    fontSize: animation1.value,                  ),                ),              ),            ],          ),          Center(            child: AnimatedOpacity(              duration: Duration(milliseconds: 2000),              curve: Curves.fastLinearToSlowEaseIn,              opacity: _containerOpacity,              child: AnimatedContainer(                duration: Duration(milliseconds: 2000),                curve: Curves.fastLinearToSlowEaseIn,                height: _width / _containerSize,                width: _width / _containerSize,                alignment: Alignment.center,                decoration: BoxDecoration(                  color: Colors.white,                  borderRadius: BorderRadius.circular(30),                ),                // child: Image.asset('assets/images/file_name.png')                child: Image.asset('assets/logo/logo_sarty.png'),              ),            ),          ),        ],      ),    );  }}class PageTransition extends PageRouteBuilder {  final Widget page;  PageTransition(this.page)      : super(          pageBuilder: (context, animation, anotherAnimation) => page,          transitionDuration: Duration(milliseconds: 2000),          transitionsBuilder: (context, animation, anotherAnimation, child) {            animation = CurvedAnimation(              curve: Curves.fastLinearToSlowEaseIn,              parent: animation,            );            return Align(              alignment: Alignment.bottomCenter,              child: SizeTransition(                sizeFactor: animation,                child: page,                axisAlignment: 0,              ),            );          },        );}class SecondPage extends StatelessWidget {  @override  Widget build(BuildContext context) {    return const ChooseLangScreen();  }}