import 'package:flutter/material.dart';import 'package:page_transition/page_transition.dart';Widget space_h(double height) {  return SizedBox(    height: height,  );}Widget space_w(double width) {  return SizedBox(    height: width,  );}push(context, widget, {replacement = false}) {  return !replacement      ? Navigator.push(          context,          PageTransition(            type: PageTransitionType.leftToRight,            duration: const Duration(milliseconds: 800),            child: widget,          ),        )      : Navigator.pushReplacement(          context,          PageTransition(            type: PageTransitionType.leftToRight,            duration: const Duration(milliseconds: 800),            child: widget,          ));}